import React, { useState } from 'react';
import APODCard from './APODCard';
import MarsRoverGallery from './MarsRoverGallery';
import NeoChart from './NeoChart';
import LoadingSpinner from './LoadingSpinner';
import './Dashboard.css';

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState('apod');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const tabs = [
    { id: 'apod', label: 'Picture of the Day', icon: 'üåü' },
    { id: 'mars', label: 'Mars Rovers', icon: 'üöÄ' },
    { id: 'neo', label: 'Near Earth Objects', icon: '‚òÑÔ∏è' }
  ];

  const handleTabChange = (tabId) => {
    setActiveTab(tabId);
    setError(null);
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'apod':
        return <APODCard />;
      case 'mars':
        return <MarsRoverGallery />;
      case 'neo':
        return <NeoChart />;
      default:
        return <APODCard />;
    }
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>üåå NASA Data Explorer</h1>
        <p>Explore the cosmos with real NASA data</p>
      </header>

      <nav className="dashboard-nav">
        {tabs.map((tab) => (
          <button
            key={tab.id}
            className={`nav-tab ${activeTab === tab.id ? 'active' : ''}`}
            onClick={() => handleTabChange(tab.id)}
          >
            <span className="tab-icon">{tab.icon}</span>
            <span className="tab-label">{tab.label}</span>
          </button>
        ))}
      </nav>

      <main className="dashboard-content">
        {error && (
          <div className="error-banner">
            <p>‚ùå {error}</p>
            <button onClick={() => setError(null)}>Dismiss</button>
          </div>
        )}

        {loading ? (
          <LoadingSpinner />
        ) : (
          renderTabContent()
        )}
      </main>
    </div>
  );
};

export default Dashboard;
