import React, { useState } from 'react';
import { useAPOD } from '../hooks/useNasaData';
import LoadingSpinner from './LoadingSpinner';
import './APODCard.css';

const APODCard = () => {
  const [selectedDate, setSelectedDate] = useState('');
  const { data, loading, error, refetch, getRandomAPOD } = useAPOD();

  const handleDateChange = (event) => {
    const date = event.target.value;
    setSelectedDate(date);
    refetch(date);
  };

  const handleRandomClick = () => {
    getRandomAPOD();
    setSelectedDate('');
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="apod-card">
      <div className="apod-controls">
        <div className="date-selector">
          <label htmlFor="apod-date">Select Date:</label>
          <input
            id="apod-date"
            type="date"
            value={selectedDate}
            onChange={handleDateChange}
            max={new Date().toISOString().split('T')[0]}
          />
        </div>
        <button className="random-btn" onClick={handleRandomClick}>
          üé≤ Random APOD
        </button>
      </div>

      {error && (
        <div className="error-message">
          <p>Error: {error}</p>
        </div>
      )}

      {data && (
        <div className="apod-content">
          <h2>{data.title}</h2>
          <p className="apod-date">üìÖ {data.date}</p>

          <div className="apod-media">
            {data.media_type === 'image' ? (
              <img
                src={data.url}
                alt={data.title}
                className="apod-image"
                loading="lazy"
              />
            ) : (
              <iframe
                src={data.url}
                title={data.title}
                className="apod-video"
                allowFullScreen
              />
            )}
          </div>

          <div className="apod-explanation">
            <h3>Explanation</h3>
            <p>{data.explanation}</p>
          </div>

          {data.copyright && (
            <p className="apod-copyright">
              üì∏ Copyright: {data.copyright}
            </p>
          )}

          {data.hdurl && (
            <a
              href={data.hdurl}
              target="_blank"
              rel="noopener noreferrer"
              className="hd-link"
            >
              üîç View in HD
            </a>
          )}
        </div>
      )}
    </div>
  );
};

export default APODCard;
